import React from "react";
import { Form, Button, Row, Col } from "react-bootstrap";

import axios from "axios";

export default function EditModal(props) {
  const handleSubmit = (event, clientId) => {
    event.preventDefault();

    const url = `http://localhost:8080/pet/updatePet/${clientId}`;

    axios
    .put(url, props.selectedClient)
    .then((res) => console.log(res))
    .catch((error) => console.log(error));
  }

  const handleInputChange = (event) => {
    event.preventDefault();

    const { name, value } = event.target;

    props.setSelectedClient((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handleInputBooleanChange = (event) => {
    event.preventDefault();

    const { name, value } = event.target;

    props.setSelectedClient({
      [name]: value === "true",
    });
  };

  return (
    <div className="CreateModal">
        <section>
          <div className="topBar">
            <Row>
              <Col>
                <h2>Edit Patient</h2>
              </Col>
              <Col className="closeButton">
                <Button onClick={props.toggleEditModal}>
                  <i className="fa fa-close fa-lg"></i>
                </Button>
              </Col>
            </Row>
          </div>
          <div className="modalContent">
            <Form
              className="form"
              onSubmit={(event) =>
                this.handleSubmit(event, props.selectedClient.clientId)
              }
            >
              <Row>
                <Col>
                  <Form.Group>
                    <Form.Label>Pet Name</Form.Label>
                    <Form.Control
                      name="petName"
                      value={selectedClient.petName}
                      onChange={handleInputChange}
                    />
                  </Form.Group>

                  <Form.Group>
                    <Form.Label>Client Name</Form.Label>
                    <Form.Control
                      name="clientName"
                      value={selectedClient.clientName}
                      onChange={handleInputChange}
                    />
                  </Form.Group>

                  <Form.Group>
                    <Form.Label>Phone Number</Form.Label>
                    <Form.Control
                      name="phoneNumber"
                      value={phoneNumber}
                      onChange={handleInputChange}
                    />
                    <Form.Text
                      className="text-muted"
                      onChange={handleInputChange}
                    >
                      Please add phone number in the following format 555
                      1117777
                    </Form.Text>
                  </Form.Group>

                  <Form.Group>
                    <Form.Label>Pet Behavior</Form.Label>
                    <Form.Control
                      name="behavior"
                      value={behavior}
                      onChange={handleInputChange}
                    />
                  </Form.Group>
                </Col>

                <Col>
                  <Form.Group>
                    <Form.Label>Fired Client?</Form.Label>
                    <Form.Control
                      as="select"
                      name="banned"
                      onChange={handleInputBooleanChange}
                      value={props.selectedClient.banned}
                    >
                      <option value={false}>No</option>
                      <option value={true}>Yes</option>
                    </Form.Control>
                  </Form.Group>
                </Col>
              </Row>
              <div className="buttonbottom">
                <Button justify-content-end variant="primary" type="submit">
                  Submit
                </Button>
              </div>
            </Form>
          </div>
        </section>
      </div>
  )
}


